'use strict';

const { cipherSuites } = require('lib/constants');

const ciphers = new Set(Object.values(cipherSuites));

module.exports = {
  isCipherSuite,
};

/**
 * Check if argument is supported cipher suite.
 * @param {number} cipher
 * @returns {boolean}
 */
function isCipherSuite(cipher) {
  return ciphers.has(cipher);
}
